const stats = $input.first().json;
const tasks = $input.item(1)?.json || [];
const events = $input.item(2)?.json || [];

const summary = {
  date: new Date().toISOString().split('T')[0],
  stats: stats,
  tasks_today: Array.isArray(tasks) ? tasks : [tasks],
  events_today: Array.isArray(events) ? events : [events],
  summary_text: `ðŸ“… Daily Summary for ${new Date().toDateString()}\n\n` +
    `Tasks due today: ${stats.tasks_due_today}\n` +
    `Tasks completed: ${stats.tasks_completed_today}\n` +
    `Reminders: ${stats.reminders_today}\n` +
    `Events: ${stats.events_today}`
};

return summary;
